window.initializeTabs=function(){const tabButtons=document.querySelectorAll('[role="tab"]');const tabPanels=document.querySelectorAll('[role="tabpanel"]');tabButtons.forEach(button=>{const newButton=button.cloneNode(true);button.parentNode.replaceChild(newButton,button);});document.querySelectorAll('[role="tab"]').forEach(button=>{button.addEventListener('click',handleTabClick);});const firstTab=document.querySelector('[role="tab"]');if(firstTab){setTimeout(()=>firstTab.click(),0);}};function handleTabClick(event){const tabButtons=document.querySelectorAll('[role="tab"]');tabButtons.forEach(btn=>{btn.classList.remove('active');btn.setAttribute('aria-selected','false');});const button=event.target;button.classList.add('active');button.setAttribute('aria-selected','true');const tabPanels=document.querySelectorAll('[role="tabpanel"]');tabPanels.forEach(panel=>{panel.classList.add('hidden');});const targetId=button.getAttribute('data-tabs-target');const targetPanel=document.querySelector(targetId);if(targetPanel){targetPanel.classList.remove('hidden');}};document.addEventListener('DOMContentLoaded',function(){const sidebar=document.getElementById('logo-sidebar');const collapseBtn=document.getElementById('sidebar-collapse-btn');const sidebarTexts=document.querySelectorAll('.sidebar-text');const mainContent=document.getElementById('main-content');const tooltipElements=document.querySelectorAll('[role="tooltip"]');function setSidebarState(collapsed){localStorage.setItem('sidebarCollapsed',collapsed);}
function getSidebarState(){return localStorage.getItem('sidebarCollapsed')==='true';}
function updateSidebarUI(collapsed){if(collapsed){sidebar.classList.remove('w-64');sidebar.classList.add('w-16');sidebarTexts.forEach(text=>text.classList.add('hidden'));mainContent.classList.remove('sm:ml-64');mainContent.classList.add('sm:ml-16');collapseBtn.querySelector('svg').classList.add('rotate-180');tooltipElements.forEach(tooltip=>{tooltip.style.display='block';});}else{sidebar.classList.add('w-64');sidebar.classList.remove('w-16');sidebarTexts.forEach(text=>text.classList.remove('hidden'));mainContent.classList.add('sm:ml-64');mainContent.classList.remove('sm:ml-16');collapseBtn.querySelector('svg').classList.remove('rotate-180');tooltipElements.forEach(tooltip=>{tooltip.style.display='none';});}}
const initialState=getSidebarState();updateSidebarUI(initialState);collapseBtn.addEventListener('click',function(){const newState=!getSidebarState();setSidebarState(newState);updateSidebarUI(newState);});});;const AppInit={initializeAll:function(){if(window.initFlowbite){window.initFlowbite();}
if(document.getElementById("line-chart")){window.initializeChart();}
if(document.querySelector('[role="tab"]')){window.initializeTabs();}}};document.addEventListener('DOMContentLoaded',AppInit.initializeAll);document.addEventListener('htmx:afterSwap',AppInit.initializeAll);document.addEventListener('htmx:afterSettle',AppInit.initializeAll);;